import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addToCart, removeFromCart } from '../actions/cartActions';
import { Link } from 'react-router-dom';

function PlaceOrderScreen (props) {

    const cart = useSelector(state => state.cart);

    const { cartItems, shipping, payment } = cart;

    if(!shipping){
        props.location.push("/shipping");
    }

    if(!payment){
        props.location.push("/payment");
    }

    const dispatch = useDispatch();

    const removeFromCartHandler = (productId) => {
        dispatch(removeFromCart(productId));
    }
    useEffect(() => {
        if (productId) {
            dispatch(addToCart(productId, qty));
        }
    }, []);

    const checkoutHandler = () => {
        props.history.push("/signin?redirect=shipping");
    }

    return <div>
        <CheckoutSteps step1 step2 step3 step4></CheckoutSteps>
        <div className="placeorder">
            <div className="placeorder-info">
                <div>
                    <h3>
                        Shipping
                    </h3>
                    <div>
                        {cart.shipping.address}, {cart.shipping.city},
                        {cart.shipping.postCode},{cart.shipping.country},
                    </div>
                </div>
                <div>
                    <h3>
                        Payment
                    </h3>
                    <div>
                        Payment Method: {cart.payment.paymentMethod}
                    </div>
                </div>
                <div>
                    <ul className="cart-list-container">
                        <li>
                            <h3>
                                Shopping Cart
                            </h3>
                            <div>
                                Price
                            </div>
                        </li>
                        {
                            cartItems.lenght ===0 ?
                            <div>
                                Cart is empty
                            </div>
                            :
                            cartItems.map( item =>
                                <li>
                                    <div className="cart-image">
                                        <img src={item.image} alt="product" />
                                    </div>
                                    <div className="cart-name">
                                        <div>
                                            <Link to={"/product/" + item.product}>
                                                {item.name}  
                                            </Link>
                                        </div>
                                        <div>
                                            Qty:
                                            <select value={item.qty} onChange={(e) => dispatch(addToCart(item.product, e.target.value))}>
                                                {[...Array(item.countInStock).keys()].map(x =>
                                                    <option key={x + 1} value={x + 1}>{x + 1}</option>
                                                )}
                                            </select>
                                            <button type="button" className="button" onClick={() => removeFromCartHandler(item.product)}>
                                                Delete
                                            </button>
                                        </div>
                                    </div>
                                    <div className="cart-price">
                                        £{item.price}
                                    </div>
                                </li>
                            )
                        }
                    </ul>
                </div>

                


            </div>
            <div className="placeholder-action">
            <h3>
                Subtotal ( {cartItems.reduce((a, c) => a + c.qty, 0)} items)
                :
                £ {cartItems.reduce((a, c) => a + c.price * c.qty, 0)}
            </h3>
                <button onClick={checkoutHandler} className="button-primary full-width" disabled={cartItems.lenght === 0}>
                    Proceed to Checkout
                </button>
                
            </div>
        </div>
    </div>
    
    
}

export default PlaceOrderScreen;
