import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { saveProduct } from '../actions/productActions';


const ProductsScreen = (props) => {
  const [modalVisable, setModalVisable] = useState(false);
  const [id, setId] = useState('');
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [image, setImage] = useState('');
  const [category, setCategory] = useState('');
  const [description, setDescription] = useState('');
  const [countInStock, setCountInStock] = useState('');
  const productList = useSelector(state => state.productList)
  const { loading, products, error } = productList;
  const productSave = useSelector(state => state.productSave)
  const { loading: loadingSave, success: successSave, error: errorSave } = productSave;
  const dispatch = useDispatch();

  useEffect(() => {
    
    return () => {
      //
    };
  }, []);
  
  const openModal = (product)=> {
        setModalVisable(true);
        setId(product._id);
        setName(product.name);
        setPrice(product.price);
        setDescription(product.description);
        setImage(product.image);
        setCategory(product.category);
        setCountInStock(product.countInStock);
  }

  const submitHandler = (e) => {
      e.preventDefault();
      dispatch(saveProduct({
          _id: id,
          name,
          price,
          image,
          category,
          countInStock,
          description,
      }));
  }

  return <div className="content content-margined">

        <div className="product-header">
            <h3>Products</h3>
            <button onClick={()=>openModal({})} >Create Product</button>
        </div>
      { modalVisable &&
            <div className="form">
                  <form onSubmit={submitHandler} >
                        <ul className="form-container">
                              <li>
                                    <h2>Add Product</h2>
                              </li>
                              <li>
                                    {loadingSave && <div>Loading...</div>}
                                    {errorSave && <div>{ error }</div>}
                              </li>
                              <li>
                                    <label htmlFor="name">Name
                                    </label>
                                    <input type="text" name="name" value={name} id="name" onChange={(e) => setName(e.target.value)}>
                                    </input>
                              </li>
                              <li>
                                    <label htmlFor="price">Price
                                    </label>
                                    <input type="text" name="price" value={price} id="price" onChange={(e) => setPrice(e.target.value)}>
                                    </input>
                              </li>
                              <li>
                                    <label htmlFor="image">Image
                                    </label>
                                    <input type="text" name="image" value={image} id="image" onChange={(e) => setImage(e.target.value)}>
                                    </input>
                              </li>
                              <li>
                                    <label htmlFor="category">Category
                                    </label>
                                    <input type="text" name="category" value={category} id="category" onChange={(e) => setCategory(e.target.value)}>
                                    </input>
                              </li>
                              <li>
                                    <label htmlFor="countInStock">Count In Stock
                                    </label>
                                    <input type="text" name="countInStock" value={countInStock} id="countInStock" onChange={(e) => setCountInStock(e.target.value)}>
                                    </input>
                              </li>
                              <li>
                                    <label htmlFor="description">Description
                                    </label>
                                    <textarea name="description" value={description} id="description" onChange={(e) => setDescription(e.target.value)}>
                                    </textarea>
                              </li>
                              <li>
                                    <button type="submit" className="button-primary">Create</button>
                              </li>
                              <li>
                                    <button type="button" onClick={() => setModalVisable(false)} className="button-secondary">Back</button>
                              </li>
                              
                        </ul>
                  </form>
            </div>
      }
      <div className="products-list">
            <table>
                  <thead>
                        <tr>
                             <th>ID</th>
                             <th>Name</th> 
                             <th>Price</th>
                             <th>Catagory</th>
                             <th>Action</th>
                        </tr>
                  </thead>
                  <tbody>
                        {products.map((product) => (
                        <tr key={product._id}>
                        <td>{product._id}</td>
                        <td>{product.name}</td>
                        <td>{product.price}</td>
                        <td>{product.category}</td>
                        <td>
                              <button onClick={() => openModal(product)}>
                              Edit
                              </button>{' '}
                              <button>Delete
                              </button>
                        </td>
                        </tr>
                        ))}
                  </tbody>
            </table>
        </div>
  </div>
}

export default ProductsScreen;
